import { all, takeEvery, call, put } from "redux-saga/effects";
import {
  firestore,
  convertCollectionsSnapshotToMap,
} from "../../firebase/firebase.utils";

import {
  fetchCollectionsSuccess,
  fetchCollectionsFailure,
} from "./shop.actions.js";

import shopActionTypes from "./shop.types";

export function* fetchCollectionsAsync(category) {
  if (category == "shirt") {
    var id = "1ISQtx97dSVjiYR472dy";
  }
  if (category == "costumes") {
    var id = "KW745JXq2Ul4RaXyNbxf";
  }
  if (category == "dresses") {
    var id = "X6zUVXBtRDGVYUiOzYLG";
  }
  if (category == "jackets") {
    var id = "1g51AWLJBrnf5Mq4s6cRv";
  }
  try {
    const collectionRef = firestore.doc(
      "shop/collection/clothing/1ISQtx97dSVjiYR472dy"
    );
    const snapshot = yield collectionRef.get();
    const shirtItems = yield snapshot.data();
    yield put(fetchCollectionsSuccess(shirtItems));
  } catch (error) {
    yield put(fetchCollectionsFailure(error.message));
  }
}

export function* fetchCollectionsStart() {
  yield takeEvery(
    shopActionTypes.FETCH_COLLECTIONS_START,
    fetchCollectionsAsync
  );
}

export function* shopSagas() {
  yield all([call(fetchCollectionsStart)]);
}
