import React, { useEffect, useRef } from "react";
import { gsap } from "gsap";
import { Draggable } from "gsap/Draggable";
import FileLoader from "../../components/file-loader/fileLoader.js";
// import $ from "jquery";
import { Resizable, ResizableBox } from "react-resizable";

import "./clothing-page.styles.scss";

gsap.registerPlugin(Draggable);

const ClothingPage = () => {
  const pictureRef = useRef(null);

  useEffect(() => {
    Draggable.create(".picture");
    // $(pictureRef).resizable();
  }, []);

  return (
    <div>
      <h1>ClothingPage</h1>
      <ResizableBox
        width={200}
        height={200}
        resizeHandles={["se"]}
        minConstraints={[100, 100]}
        maxConstraints={[300, 300]}
      >
        <img
          // ref={pictureRef}
          className="picture"
          src={require("./png-pictures/cloth1.png")}
        ></img>
      </ResizableBox>

      <img
        // ref={pictureRef}
        className="picture"
        src={require("./png-pictures/cloth2.png")}
      ></img>
      <FileLoader />
    </div>
  );
};

export default ClothingPage;
