import React, { useEffect } from "react";
import { connect } from "react-redux";
import { createStructuredSelector } from "reselect";

import "./collection.styles.scss";

import { convertCollectionsToMap } from "../../firebase/firebase.utils";
import {
  selectCollections,
  selectIsCollectionFectching,
} from "../../redux/shop/shop.selectors";
import { fetchCollectionsStart } from "../../redux/shop/shop.actions";

const CollectionPage = ({ shirtItems, isFetching, fetchCollectionsStart }) => {
  useEffect(async () => {
    await fetchCollectionsStart();
  }, [fetchCollectionsStart]);

  return (
    <div>
      <h1 className="collectionPageTitle">Collection Page</h1>
      {!isFetching && console.log(shirtItems)}
    </div>
  );
};

const mapStateToProps = createStructuredSelector({
  shirtItems: selectCollections,
  isFetching: selectIsCollectionFectching,
});
const mapDispatchToProps = (dispatch) => ({
  fetchCollectionsStart: () => dispatch(fetchCollectionsStart()),
});

export default connect(mapStateToProps, mapDispatchToProps)(CollectionPage);
