import styled from "styled-components";
import { gsap } from "gsap";

export const MinusSign = styled.img`
  position: absolute;
  width: 20px;
  height: 6px;
  right: 360px;
  top: 369px;

  display: flex;

  color: #000000;
  cursor: pointer;
`;
export const PlusSign = styled.img`
  position: absolute;
  width: 17px;
  height: 12px;
  right: 305px;
  top: 366px;

  display: flex;

  color: #000000;
  cursor: pointer;
`;
export const Quantity = styled.h2`
  position: absolute;

  height: 16.66px;
  right: 338px;
  top: 350px;
  opacity:0

  font-family: Bahnschrift;
  font-style: normal;
  font-weight: normal;
  font-size: 14px;
  line-height: 17px;

  /* identical to box height */

  color: #000000;
`;

gsap.to("Quantity", 1, { scaleY: 2 });
