import React from "react";
import { connect } from "react-redux";
import { createStructuredSelector } from "reselect";

import CartIcon from "../cart-icon/cart-icon.component";
import CartDropdown from "../cart-dropdown/cart-dropdown.component";
import { selectCartHidden } from "../../redux/cart/cart.selectors";
import { selectCurrentUser } from "../../redux/user/user.selectors";
import { selectCartItems } from "../../redux/cart/cart.selectors";

import { ReactComponent as Logo } from "../../assets/logo.svg";
import { ReactComponent as Hamburger } from "../../assets/ham.svg";
import { ReactComponent as Delivery } from "../../assets/delivery.svg";
import { ReactComponent as Cart } from "../../assets/cart.svg";

import { fetchCartItemsFromFirestoreStart } from "../../redux/cart/cart.actions";

import {
  HeaderContainer,
  LinkContainer,
  OptionsContainer,
  OptionLink,
} from "./header.styles";

import { signOutStart } from "../../redux/user/user.actions";

const Header = ({
  currentUser,
  hidden,
  signOutStart,
  cartItems,
  fetchCartItemsFromFirestoreStart,
}) => (
  <HeaderContainer>
    <LinkContainer to="/">
      <Logo className="logo" />

      <Hamburger className="ham" />
    </LinkContainer>

    <Delivery className="delivery" />

    <CartIcon />

    {hidden ? null : <CartDropdown />}
  </HeaderContainer>
);

const mapStateToProps = createStructuredSelector({
  currentUser: selectCurrentUser,
  hidden: selectCartHidden,
  cartItems: selectCartItems,
});

const mapDispatchToProps = (dispatch) => ({
  signOutStart: (cartItems) => dispatch(signOutStart(cartItems)),
  fetchCartItemsFromFirestoreStart: () =>
    dispatch(fetchCartItemsFromFirestoreStart()),
});

export default connect(mapStateToProps, mapDispatchToProps)(Header);
