import { all, put, takeLatest, call } from "redux-saga/effects";

import { getCurrentUser, firestore } from "../../firebase/firebase.utils";

import AdminActionTypes from "./admin.types";

export function* fetchOrdersFromFirestoreAsync() {
  try {
    const userRef = yield firestore.collection(`admin/customers/orders`);
    const snapshot = yield userRef.get();
    const docs = yield snapshot.docs;
    yield docs.map((doc) => {
      const data = doc.data();
      const unixTime = new Date(data.token.created * 1000);
      const time = unixTime.toLocaleString();
      const dataArray = () => ({
        email: data.token.email,
        itemsPurchased: data.purchasedItems,
        time: time,
        addresses: {
          city: data.token.card.address_city,
          country: data.token.card.address_country,
          address_line1: data.token.card.address_line1,
          address_line1_check: data.token.address_line1_check,
          address_line2: data.token.card.address_line2,
          state: data.token.card.address_state,
          zip: data.token.card.address_zip,
          zip_check: data.token.card.address_zip_check,
          country: data.token.card.country,
        },
        card: {
          brand: data.token.card.brand,
          funding: data.token.card.funding,
        },
      });
      console.log(data);
    });
  } catch (error) {
    console.log(error);
  }
}

export function* onFetchOrdersFromFirestoreStart() {
  yield takeLatest(
    AdminActionTypes.FETCH_ORDERS_FROM_FIRESTORE_START,
    fetchOrdersFromFirestoreAsync
  );
}

export function* adminSagas() {
  yield all([call(onFetchOrdersFromFirestoreStart)]);
}
